export default {
  name: 'App',
  components: {
    ChartCard: {
      template: `
        <el-card class="chart-card" :body-style="{ padding: '20px' }">
          <div ref="chart" class="chart" style="height: 400px;"></div>
        </el-card>
      `,
      props: ['options'],
      mounted() {
        const chart = echarts.init(this.$refs.chart);
        chart.setOption(this.options);
      }
    }
  },
  data() {
    return {
      turnoverOptions: {
        tooltip: { trigger: 'axis', axisPointer: { type: 'shadow' }},
        xAxis: { type: 'category', data: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'] },
        yAxis: { type: 'value' },
        series: [{ data: [120, 200, 150, 80, 70, 110], type: 'bar' }]
      },
      categoryOptions: {
        tooltip: { trigger: 'item' },
        series: [{ type: 'pie', data: [
          { value: 335, name: 'Category A' },
          { value: 310, name: 'Category B' },
          { value: 234, name: 'Category C' },
          { value: 135, name: 'Category D' },
          { value: 1548, name: 'Category E' }
        ]}]
      },
      regionOptions: {
        tooltip: { trigger: 'item' },
        series: [{ type: 'map', map: 'china', data: [
          { name: 'Beijing', value: Math.random() * 1000 },
          { name: 'Shanghai', value: Math.random() * 1000 },
          { name: 'Guangdong', value: Math.random() * 1000 }
        ]}]
      },
      priceTrendOptions: {
        tooltip: { trigger: 'axis' },
        xAxis: { type: 'category', data: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'] },
        yAxis: { type: 'value' },
        series: [{ type: 'line', data: [500, 400, 450, 550, 500, 600] }]
      },
      activityOptions: {
        tooltip: { trigger: 'axis' },
        xAxis: { type: 'category', data: ['00:00', '06:00', '12:00', '18:00'] },
        yAxis: { type: 'value' },
        series: [{ type: 'line', data: [30, 200, 100, 400] }]
      },
      successRateOptions: {
        tooltip: { formatter: '{a} <br/>{b} : {c}%' },
        series: [{ name: 'Success Rate', type: 'gauge', detail: { formatter: '{value}%' }, data: [{ value: 78, name: 'Success Rate' }] }]
      }
    };
  }
}
